INSERT INTO public.posts (title,"source",tags,description,image_url,image_reference) VALUES ('HTML SSE: Simplifying Real-Time Web Communication','https://symmetrical-carnival.s3.ap-south-1.amazonaws.com/publicprefix/sse.md','{"html","sse","web development"}','Learn how to set up HTML Server-Sent Events (SSE) using Node.js. SSE provides a simple and efficient way to establish real-time communication between the server and client in web applications. Follow this step-by-step guide to integrate SSE into your Node.js project and enable instant updates and notifications for your users.','https://symmetrical-carnival.s3.ap-south-1.amazonaws.com/publicprefix/volodymyr-hryshchenko-V5vqWC9gyEU-unsplash.jpg','https://unsplash.com/photos/V5vqWC9gyEU');